mapper.select:
SELECT

FROM sys_role


		and office_id = #{po.officeId}


		and name = #{po.name}


		and enname = #{po.enname}


		and role_type = #{po.roleType}


		and data_scope = #{po.dataScope}


		and is_sys = #{po.isSys}


		and useable = #{po.useable}


		and create_by = #{po.createBy}


		and create_date = #{po.createDate}


		and update_by = #{po.updateBy}


		and update_date = #{po.updateDate}


		and remarks = #{po.remarks}


		and del_flag = #{po.delFlag}




		ORDER BY ${page.orderBy}


		ORDER BY id desc



limit #{page.limitStart},#{page.pageSize}

SELECT
count(1) cnt
FROM sys_role


		and office_id = #{po.officeId}


		and name = #{po.name}


		and enname = #{po.enname}


		and role_type = #{po.roleType}


		and data_scope = #{po.dataScope}


		and is_sys = #{po.isSys}


		and useable = #{po.useable}


		and create_by = #{po.createBy}


		and create_date = #{po.createDate}


		and update_by = #{po.updateBy}


		and update_date = #{po.updateDate}


		and remarks = #{po.remarks}


		and del_flag = #{po.delFlag}




		ORDER BY ${page.orderBy}


		ORDER BY id desc

