mapper.select:
SELECT

FROM u_user_cookie


		and user_id = #{po.userId}


		and user_login_id = #{po.userLoginId}


		and type = #{po.type}


		and cookie_name = #{po.cookieName}


		and cookie_value = #{po.cookieValue}


		and domain = #{po.domain}


		and path = #{po.path}


		and expiration_time = #{po.expirationTime}


		and is_available = #{po.isAvailable}


		and is_deleted = #{po.isDeleted}


		and version_no = #{po.versionNo}


		and create_userid = #{po.createUserid}


		and create_username = #{po.createUsername}


		and create_userip = #{po.createUserip}


		and create_usermac = #{po.createUsermac}


		and create_time = #{po.createTime}


		and create_time_db = #{po.createTimeDb}


		and server_ip = #{po.serverIp}


		and update_userid = #{po.updateUserid}


		and update_username = #{po.updateUsername}


		and update_userip = #{po.updateUserip}


		and update_usermac = #{po.updateUsermac}


		and update_time = #{po.updateTime}


		and update_time_db = #{po.updateTimeDb}


		and client_versionno = #{po.clientVersionno}


		and company_id = #{po.companyId}


		and platform_id = #{po.platformId}




		ORDER BY ${page.orderBy}


		ORDER BY id desc



limit #{page.limitStart},#{page.pageSize}

