mapper.select:
SELECT

FROM u_menu


		and parent_id = #{po.parentId}


		and name = #{po.name}


		and sort = #{po.sort}


		and href = #{po.href}


		and target = #{po.target}


		and icon = #{po.icon}


		and is_show = #{po.isShow}


		and permission = #{po.permission}


		and create_by = #{po.createBy}


		and create_date = #{po.createDate}


		and update_by = #{po.updateBy}


		and update_date = #{po.updateDate}


		and remarks = #{po.remarks}


		and del_flag = #{po.delFlag}




		ORDER BY ${page.orderBy}


		ORDER BY id desc



limit #{page.limitStart},#{page.pageSize}

SELECT
count(1) cnt
FROM u_menu


		and parent_id = #{po.parentId}


		and name = #{po.name}


		and sort = #{po.sort}


		and href = #{po.href}


		and target = #{po.target}


		and icon = #{po.icon}


		and is_show = #{po.isShow}


		and permission = #{po.permission}


		and create_by = #{po.createBy}


		and create_date = #{po.createDate}


		and update_by = #{po.updateBy}


		and update_date = #{po.updateDate}


		and remarks = #{po.remarks}


		and del_flag = #{po.delFlag}




		ORDER BY ${page.orderBy}


		ORDER BY id desc

SELECT

FROM u_menu


		and parent_id = #{po.parentId}


		and name = #{po.name}


		and sort = #{po.sort}


		and href = #{po.href}


		and target = #{po.target}


		and icon = #{po.icon}


		and is_show = #{po.isShow}


		and permission = #{po.permission}


		and create_by = #{po.createBy}


		and create_date = #{po.createDate}


		and update_by = #{po.updateBy}


		and update_date = #{po.updateDate}


		and remarks = #{po.remarks}


		and del_flag = #{po.delFlag}


order by parent_id,sort
