mapper.select:
SELECT

FROM u_company


		and company_name like concat('%',#{po.companyName},'%') 


		and company_celebrate_time = #{po.companyCelebrateTime}


		and company_mail_suffix = #{po.companyMailSuffix}


		and registration_status = #{po.registrationStatus}


		and company_content = #{po.companyContent}


		and create_time = #{po.createTime}


		and update_time = #{po.updateTime}


		and platform_id = #{po.platformId}




		ORDER BY ${page.orderBy}


		ORDER BY id desc		



limit #{page.limitStart},#{page.pageSize}

SELECT
count(1) cnt
FROM u_company


