SELECT

FROM u_user


		and id = #{po.id}


		and login_name = #{po.loginName}


		and password = #{po.password}


		and mobile = #{po.mobile}


		and mail = #{po.mail}


		and qrcode = #{po.qrcode}


		and salt = #{po.salt}


		and update_salt = #{po.updateSalt}


		and company_id = #{po.companyId}


		and is_available = #{po.isAvailable}


		and is_deleted = #{po.isDeleted}


		and create_time = #{po.createTime}


		and update_time = #{po.updateTime}




		ORDER BY ${page.orderBy}


		ORDER BY id desc		



limit #{page.limitStart},#{page.pageSize}

SELECT
count(1) cnt
FROM u_user


		and login_name = #{po.loginName}


		and password = #{po.password}


		and mobile = #{po.mobile}


		and mail = #{po.mail}


		and qrcode = #{po.qrcode}


		and salt = #{po.salt}


		and update_salt = #{po.updateSalt}


		and company_id = #{po.companyId}


		and is_available = #{po.isAvailable}


		and is_deleted = #{po.isDeleted}


		and update_time = #{po.updateTime}


		and create_time > #{po.beginTime}


		and #{po.finishTime} > create_time

SELECT
u.id,
u.login_name,
u.password,
u.mobile,
u.mail,
u.qrcode,
u.salt,
u.update_salt,
u.company_id,
u.create_time
FROM u_user u
LEFT JOIN u_user_extend ue ON u.id = ue.id


		and u.id = #{po.id}


		and u.login_name = #{po.loginName}


		and u.mobile = #{po.mobile}


		and u.mail = #{po.mail}


		and u.qrcode = #{po.qrcode}


		and u.salt = #{po.salt}


		and u.update_salt = #{po.updateSalt}


		and u.company_id = #{po.companyId}


		and u.is_available = #{po.isAvailable}


		and u.is_deleted = #{po.isDeleted}


		and u.create_time > #{po.beginTime}


		and #{po.finishTime} > u.create_time


		and ue.name = #{po.name}




		ORDER BY ${page.orderBy}


		ORDER BY u.id



limit #{page.limitStart},#{page.pageSize}

