SELECT

FROM u_role


		and id = #{po.id}


		and name = #{po.name}


		and enname = #{po.enname}


		and role_type = #{po.roleType}


		and is_sys = #{po.isSys}


		and useable = #{po.useable}


		and remarks = #{po.remarks}


		and platform_id = #{po.platformId}


		and create_time = #{po.createTime}


		and update_time = #{po.updateTime}




		ORDER BY ${page.orderBy}


		ORDER BY id desc		



limit #{page.limitStart},#{page.pageSize}

